   public static void printMergeArray(int [] A, int low, int mid, int high, int n) {

      int i;

      for (i = 1; i < low; i++)
         sop(A[i] + " ");

      sop("| ");

      for (i = low; i <= mid; i++)
         sop(A[i] + " ");

      sop("| ");

      for (i = mid+1; i <= high; i++)
         sop(A[i] + " ");

      sop("| ");

      for (i = high + 1; i <= n; i++)
         sop(A[i] + " ");

   }

   public static void printPartitionArray(int [] A, int low, int high, int n) {

      int i;

      for (i = 1; i < low; i++)
         sop(A[i] + " ");

      sop("| ");

      for (i = low; i <= high; i++)
         sop(A[i] + " ");

      sop("| ");


      for (i = high + 1; i <= n; i++)
         sop(A[i] + " ");

   }

